	member_pw=#{memberPw}
	,member_address=#{memberAddress}
	,member_email=#{memberEmail}
	,member_phone=#{memberPhone}
	,member_account_status=#{memberAccountStatus}
	,member_incentive=#{memberIncentive}
WHERE
 	member_id=#{memberId};

