mapper.select:
SELECT ssc.stock_sector_check_code , ssc.stock_code , p.product_name , ssc.warehouse_code , ws.sector_code , ssb.stock_amount , ssc.stock_check_error_amount , ssc.member_id , ssc.stock_check_detail , ssc.stock_check_date , ssc.stock_check_reg_date FROM tb_stock_sector_check AS ssc INNER JOIN tb_stock_status AS ssb ON ssc.stock_code = ssb.stock_code INNER JOIN tb_product AS p ON ssb.product_code = p.product_code INNER JOIN tb_warehouse AS w ON ssc.warehouse_code = w.warehouse_code INNER JOIN tb_warehouse_sector AS ws ON ws.warehouse_code = w.warehouse_code WHERE ssc.warehouse_code=#{warehouse_code}
AND ws.sector_code =#{sector_code}
;
