mapper.insert:
INSERT INTO 
	tb_car(
	car_code
	, car_kind
	, car_status
	, car_status_detail
	, car_using_year
	, car_num
	, car_using_status
	, ware_admin_id
	, car_reg_date
	, car_update_date
)VALUES(
	sf_newCarCode()
	, #{carKind}
	, #{carStatus}
	, #{carStatusDetail}
	, #{carUsingYear}
	, #{carNumber}
	, #{carUsingStatus}
	, #{wareAdminId}
	, NOW()
	, NOW());

mapper.update:
UPDATE tb_car
SET
	car_kind= #{carKind}
	, car_status= #{carStatus}
	, car_status_detail=#{carStatusDetail}
	, car_using_year= #{carUsingYear}
	, car_num= #{carNumber}
	, car_using_status= #{carUsingStatus}
	, ware_admin_id= #{wareAdminId}
	, car_update_date=NOW()
WHERE 
	car_code= #{carCode};
